import { useEffect, useState, useRef, useCallback } from "react";
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  CircularProgress,
  CardActionArea,
  TextField,
  Box,
} from "@mui/material";

const PAGE_SIZE = 9;

function App() {
  const [jobs, setJobs] = useState([]);
  const [visibleJobs, setVisibleJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [locationFilter, setLocationFilter] = useState("");
  const [page, setPage] = useState(1);
  const observerRef = useRef(null);

  const fetchJobs = async (location = "") => {
    try {
      setLoading(true);
      await fetch("/scrape");
      const res = await fetch(`/jobs${location ? `?location=${encodeURIComponent(location)}` : ""}`);
      const data = await res.json();
      setJobs(data);
      setVisibleJobs(data.slice(0, PAGE_SIZE));
      setPage(1);
    } catch (err) {
      console.error("Error fetching jobs:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchJobs();
  }, []);

  const loadMore = useCallback(() => {
    const nextPage = page + 1;
    const nextJobs = jobs.slice(0, nextPage * PAGE_SIZE);
    setVisibleJobs(nextJobs);
    setPage(nextPage);
  }, [jobs, page]);

  // Infinite scroll trigger
  const observerCallback = useCallback(
    (entries) => {
      const [entry] = entries;
      if (entry.isIntersecting && visibleJobs.length < jobs.length) {
        loadMore();
      }
    },
    [visibleJobs, jobs, loadMore]
  );

  useEffect(() => {
    const observer = new IntersectionObserver(observerCallback, {
      root: null,
      threshold: 1.0,
    });
    if (observerRef.current) observer.observe(observerRef.current);
    return () => {
      if (observerRef.current) observer.unobserve(observerRef.current);
    };
  }, [observerCallback]);

  const handleLocationChange = (e) => {
    setLocationFilter(e.target.value);
  };

  const handleLocationSearch = (e) => {
    e.preventDefault();
    fetchJobs(locationFilter);
  };

  return (
    <Container sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>Rippling Engineering Jobs</Typography>

      <Box component="form" onSubmit={handleLocationSearch} sx={{ mb: 3 }}>
        <TextField
          label="Filter by location"
          value={locationFilter}
          onChange={handleLocationChange}
          variant="outlined"
          size="small"
        />
      </Box>

      {loading ? (
        <CircularProgress />
      ) : (
        <Grid container spacing={3}>
          {visibleJobs.length === 0 ? (
            <Typography>No jobs found.</Typography>
          ) : (
            visibleJobs.map((job, idx) => (
              <Grid item xs={12} sm={6} md={4} key={idx}>
                <Card sx={{ height: '100%' }}>
                  <CardActionArea
                    component="a"
                    href={job.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    sx={{ height: '100%' }}
                  >
                    <CardContent>
                      <Typography variant="h6">{job.title}</Typography>
                      <Typography variant="body2" color="text.secondary">
                        {job.location}
                      </Typography>
                    </CardContent>
                  </CardActionArea>
                </Card>
              </Grid>
            ))
          )}
          {/* Infinite scroll sentinel */}
          <div ref={observerRef} style={{ height: 1 }} />
        </Grid>
      )}
    </Container>
  );
}

export default App;
